import { useState} from 'react';
import {Link,  useNavigate} from "react-router-dom";
import Validation from "./SignupValidation"
import axios from "axios";
import {Icon} from 'react-icons-kit';
import {eyeOff} from 'react-icons-kit/feather/eyeOff';
import {eye} from 'react-icons-kit/feather/eye'
// import { UserContext } from "./UserContext";



export default function Register() {
    // useEffect(()=> {
    //     if(userInfo != null) {
    //         navigate("/")
    //     }
    // }, [])

    // const {userInfo} = useContext(UserContext)
    const [password, setPassword] = useState("");
    const [type, setType] = useState('password');
    const [icon, setIcon] = useState(eyeOff);

    const [values, setValues] = useState({
        username: undefined,
        email: undefined,
        password: undefined,
    });

    const navigate = useNavigate();
    const[errors, setErrors] = useState({})

    const handleInput = (event) => {
        setValues(prev => ({...prev, [event.target.name]:[event.target.value]}))
    }

    const handleToggle = () => {
        if (type==='password'){
           setIcon(eye);
           setType('text')
        } else {
           setIcon(eyeOff)
           setType('password')
        }
     }

    const handleSubmit =   (event) => {

        event.preventDefault();
        setErrors(Validation(values))
        if(errors.username === "" && errors.email === "" && errors.password === "") {
            axios.post('http://localhost:7078/api/auth/register', values)
                .then(res => {
                    // console.log(res)
                    navigate("/login")
                })
                .catch(err => console.log(err));
        }       
    }



    return(
        <div className = "d-flex justify-content-center align-item-center bg-white vh-50  ">
        <div className = 'p-3 rounded w-75 border border-dark'>
            <h2>Register</h2>
            <form action = "" onSubmit = {handleSubmit}>
                <div className = "mb-3" > 
                    <label htmlFor = "username" ><strong>Username</strong></label>
                    <input type = "text" placeholder = "Enter your username" 
                    onChange = {handleInput} name = "username" className = 'form-control'/>
                    {errors.username && <span className = 'text-danger'>{errors.username}</span>}
                </div>
                <div className = "mb-3" > 
                    <label htmlFor = "email" ><strong>Email</strong></label>
                    <input type = "text" placeholder = "Enter your email" 
                    onChange = {handleInput} name = "email" className = 'form-control' />
                    {errors.email && <span className = 'text-danger'>{errors.email}</span>}
                </div>
                <div className= "mb-3">
                    <label htmlFor = "password" ><strong>Password</strong></label>
                    <input type = "password" placeholder = "Enter your password"
                    onChange = {handleInput} name = "password" className = 'form-control'/>
                    <span class="flex justify-around items-center" onClick={handleToggle}>
                    <Icon class="absolute mr-10" icon={icon} size={25}/>
                    </span>
                    {errors.password && <span className = 'text-danger'>{errors.password}</span>}
                </div>
                <button type = "submit" className = "btn btn-success w-100">Sign up</button>
                <p>You are aggred to our terms and conditions</p>
                <Link to = "/login" className = "btn btn-default border bg-light text-decoration-zero w-100">Login</Link>
            </form>
        </div>
    </div>
)
}
