import { useContext, useEffect, useState} from "react";
import { useParams } from "react-router-dom";
import axios from 'axios';
import {formatISO9075} from "date-fns";
import { UserContext } from "./UserContext";
import { Link } from "react-router-dom";

export default function PostPage() {
    const[postInfo, setPostInfo] = useState();
    const{userInfo} = useContext(UserContext)
    const {id} = useParams();
    useEffect(() => {
        const axiosInstance = axios.create({withCredentials: true, headers: {
            "Content-type": "application/json",
        }})
        axiosInstance.get(`http://localhost:7078/post/${id}`)
        .then(res => {
            setPostInfo(res)
        })
        .catch(err => console.log(err))
    }, [])
    if(!postInfo) return "";
    return (
        <div className = "post-page">
             <h1>{postInfo.data.title}</h1>
             <time>{formatISO9075(new Date(postInfo.data.createdAt))}</time>
             <div className = "author">by @{postInfo.data.author.username}</div>
             {userInfo.id === postInfo.data.author._id && (
                <div className = "edit-row">
                    <Link className = "edit-btn" to = {`/edit/${postInfo.data._id}`}>
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
                             <path strokeLinecap="round" strokeLinejoin="round" d="m16.862 4.487 1.687-1.688a1.875 1.875 0 1 1 2.652 2.652L10.582 16.07a4.5 4.5 0 0 1-1.897 1.13L6 18l.8-2.685a4.5 4.5 0 0 1 1.13-1.897l8.932-8.931Zm0 0L19.5 7.125M18 14v4.75A2.25 2.25 0 0 1 15.75 21H5.25A2.25 2.25 0 0 1 3 18.75V8.25A2.25 2.25 0 0 1 5.25 6H10" />
                        </svg> Edit this post
                    </Link>

                </div>
             )}
            <div className = "image">
                <img src={`http://localhost:7078/${postInfo.data.cover}`} alt = "" />
            </div>       
            <div className = "content" dangerouslySetInnerHTML = {{__html:postInfo.data.content}} />
        </div>
    )
}
